using System.Security.Cryptography.X509Certificates;

namespace Test
{
    internal static class Certificates
    {
        private const string CertumRoot = "";
        private const string SSLcomIntermediate = "";
        private const string SSLcomNewRoot = "";
        private const string SSLcomTimestampingIntermediate = "";
        private const string SSLcomTimestampingLeaf = "";

        internal static X509Certificate2 GetCertumRoot() => GetCertificate(CertumRoot);
        internal static X509Certificate2 GetSSLcomIntermediate() => GetCertificate(SSLcomIntermediate);
        internal static X509Certificate2 GetSSLcomNewRoot() => GetCertificate(SSLcomNewRoot);
        internal static X509Certificate2 GetSSLcomTimestampingIntermediate() => GetCertificate(SSLcomTimestampingIntermediate);
        internal static X509Certificate2 GetSSLcomTimestampingLeaf() => GetCertificate(SSLcomTimestampingLeaf);

        private static X509Certificate2 GetCertificate(string base64)
        {
            return new X509Certificate2(Convert.FromBase64String(base64));
        }
    }
}